mapper.select:

select

from dbo.SUS_TRAMA_INTERBANK
where COD_TRAMA = #{codTrama,jdbcType=NUMERIC}
and NRO_CERTIFICADO = #{nroCertificado,jdbcType=VARCHAR}

mapper.delete:

delete from dbo.SUS_TRAMA_INTERBANK
where COD_TRAMA = #{codTrama,jdbcType=NUMERIC}
and NRO_CERTIFICADO = #{nroCertificado,jdbcType=VARCHAR}
